import { Button,Dialog,DialogActions,DialogContent,DialogContentText,DialogTitle } from "@mui/material";
import React, { useState , useEffect} from "react";
import {useSelector , useDispatch  } from 'react-redux'
import { getCustomers } from "../../redux/slices/appointments/thunks";
import {Box,Container,Card,Grid,CardContent,Typography} from "@mui/material";
//import {useSatate} from 'react';


const Customers = ()=>{

    const {customers} = useSelector(state => state.customer)
    const dispatch = useDispatch();

    useEffect(()=>{

        dispatch(getCustomers());

    },[dispatch])

    useEffect(()=>{

        console.log(customers);

    },[customers])
    
   
      
    return (
        
        <Container maxWidth='xl' sx={{
            border: 'solid 2px red'
        }}>
            <Box 
            sx={{
                display: 'flex',
                flexDirection: 'column'
            }}>
            {customers.length > 0 ? (
                <Grid container spacing={2}>
                    {customers.map((customer, index) => (
                        <Grid item xs={12} sm={6} md={4} key={index}>
                            <Card variant="outlined">
                                <CardContent>
                                    <Typography variant="h5" component="div">
                                        {customer.name}
                                    </Typography>
                                    <Typography color="text.secondary">
                                        DNI: {customer.dni}
                                    </Typography>
                                    {/* Agrega más detalles del cliente aquí según tu estructura de datos */}
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            ) : (
                <p>El array está vacío</p>
            )}
            </Box>
        </Container>
        
)}

export default Customers;
