import React, { useEffect, useState } from "react";
import { useSelector , useDispatch } from "react-redux";
import { getServices } from "../../redux/slices/appointments/thunks";
import { Box, Container, Card, Grid, CardContent, Typography, Button, TextField } from "@mui/material";






const Services = () => {

    const { services } = useSelector((state)=>state.services);
    const dispatch = useDispatch();

    useEffect(()=>{

    dispatch(getServices());        

    },[dispatch])


    return(
        <Container maxWidth="xl" style={{ height: '100vh', overflow: 'hidden' }}>
            <Box sx={{ maxHeight: '100%', overflowY: 'auto' ,height: '100%', width: '100%' }}>
                {services.length > 0 ? (
                    <Grid item xs={12} key={index}>
                        {services.map((service,index)=>(
                            <Grid item xs={12} key={index}>
                                <Card  variant="outlined" >
                                    <CardContent  sx={{
                                        display: 'flex',
                                        flexDirection: 'row',
                                        gap: '20px',
                                        justifyContent: 'center',
                                        alignItems: 'center'
                                    }}>
                                        <Typography variant="h5" component="div" sx={{fontSize: '1rem'}}>
                                             {service.name}
                                        </Typography>
                                    </CardContent>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>
                ) : (
                    <p>El array esta vac√≠o</p>
                )}
            </Box>
        </Container>
    )

    

};


export default Services;
