import react, {useState , useEffect} from 'react';
import { UseSelector ,  useDispatch, useSelector } from "react-redux";
import { FormControl, TextField, Button ,Snackbar, Select} from "@mui/material";
import { Box, Container } from "@mui/system";
import { useFormik } from 'formik';
import { getServices , postNewProfessional} from '../../redux/slices/appointments/thunks';

const initialValues = {
    name: '',
    dni: '',
    phone: '',
    mail: ''
}

const validate = (values) => {
    let errors = {};

    if(!values.name){
        errors.name = 'El campo es requerido';
    }else if(!/^[a-zA-Z\s\W]+$/.test(values.name)){
        errors.name = 'El campo solo admite letras';
    }

    if(!values.dni){
        errors.dni = 'El campo es requerido';
    }else if(!/^\d+$/.test(values.dni))

    if(!values.phone){
        errors.phone = 'El campo es requerido';
    }else if(!/^\d+$/.test(values.phone)){
        errors.phone = 'El campo solo admite numeros'
    }

    if(!values.mail){
        errors.mail = 'El campo es requerido';
    }else if(!/^[\w-]+(\.[\w-]+)*@([\w-]+\.)+[a-zA-Z]{2,7}$/.test(values.mail)){
        errors.mail = 'Formato de correo incorrecto'
    }
}

const NewProfessionalForm = () => {
    const newProfessional = useSelector((state)=>state.newProfessional.newProfessional)
    const allServices = useSelector((state)=> state.services.services)
    const [selectedServices , setSelectedservices] = useState([]);
    const [openSnackbar , setOpenSnackBar] = useState(false);
    const [alertMessage , setAlertMessage] = useState('');
    const [sverity , setSeverity] = useState('success');
    console.log((allServices));
    console.log((selectedServices));

    const dispatch = useDispatch();

    useEffect(()=>{

        dispatch(getServices());

    },[])

    const handleSubmit = (values, {resetForm}) => {

        dispatch(postNewProfessional(values.name,values.dni,values.phone,values.mail,services))

    };
    
    const formik = useFormik({
        initialValues,
        onSubmit: handleSubmit,
        validate
    });

    return(
        <Container>
          <form>
            <FormControl>
                <TextField
                label='Nombre'
                name="name"
                onChange={formik.handleChange}
                value={formik.values.name}
                variant="outlined"
                sx={{ mb: 2 }} 
                />
                <TextField
                label='DNI'
                name='dni'
                onChange={formik.handleChange}
                value={formik.values.dni}
                variant='outlined'
                sx={{ mb: 2 }} 
                />
                <TextField
                label='Telefono'
                name='phone'
                onChange={formik.handleChange}
                value={formik.values.phone}
                variant='outlined'
                sx={{ mb: 2 }} 
                />
                <TextField
                label='Mail'
                name='mail'
                onChange={formik.handleChange}
                value={formik.values.mail}
                variant='outlined'
                sx={{ mb: 2 }} />
                <Select>Servicios</Select>
            </FormControl>
          </form>
        </Container>
    )



};

export default NewProfessionalForm;
