import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getCustomers } from "../../redux/slices/appointments/thunks";
import { Box, Container, Card, Grid, CardContent, Typography, Button, TextField } from "@mui/material";

const Customers = () => {
  const { customers } = useSelector((state) => state.customer);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getCustomers());
  }, [dispatch]);

  useEffect(() => {
    console.log(customers);
  }, [customers]);

  const [editingCustomer, setEditingCustomer] = useState(null);

  const handleEdit = (customer) => {
    setEditingCustomer(customer);
  };

  const handleSave = (customer) => {
    // Aquí puedes enviar los cambios del cliente al servidor o realizar las acciones necesarias
    setEditingCustomer(null);
  };

  const handleCancel = () => {
    setEditingCustomer(null);
  };

  return (
    <Container maxWidth="xl" style={{ height: '100vh', overflow: 'hidden' }}>
      <Box sx={{ maxHeight: '100%', overflowY: 'auto' ,height: '100%', width: '100%' }}>
        {customers.length > 0 ? (
          <Grid container spacing={2}>
            {customers.map((customer, index) => (
              <Grid item xs={12} key={index}>
                <Card variant="outlined">
  <CardContent sx={{
    display: 'flex',
    flexDirection: 'column', // Cambia la dirección a columna para alinear los elementos verticalmente
    gap: '20px', // Añade espacio entre los elementos
    justifyContent: 'center',
    alignItems: 'center', // Alinea los elementos horizontalmente en el centro
  }}>
    {editingCustomer === customer ? (
      <>
        {/* Campos de texto para editar */}
      </>
    ) : (
      <>
        {/* Detalles del cliente */}
        <Typography variant="h5" component="div">
          {customer.name}
        </Typography>
        <Typography color="text.secondary">
          DNI: {customer.dni}
        </Typography>
        {/* Otros detalles del cliente */}
      </>
    )}
    {/* Botones de acción */}
    <Box sx={{ marginTop: 'auto' }}> {/* Añade un margen superior automático para que los botones siempre estén alineados en la parte inferior */}
      {editingCustomer === customer ? (
        <>
          <Button onClick={() => handleSave(customer)}>Guardar</Button>
          <Button onClick={handleCancel}>Cancelar</Button>
        </>
      ) : (
        <Button onClick={() => handleEdit(customer)}>Editar</Button>
      )}
    </Box>
  </CardContent>
</Card>
              </Grid>
            ))}
          </Grid>
        ) : (
          <p>El array está vacío</p>
        )}
      </Box>
    </Container>
  );
};

export default Customers;
