import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getCustomers } from "../../redux/slices/appointments/thunks";
import { Box, Container, Card, Grid, CardContent, Typography, Button, TextField } from "@mui/material";
import { updateCustomer } from "../../redux/slices/appointments/thunks";

const Customers = () => {
  const { customers } = useSelector((state) => state.customer);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getCustomers());
  }, [dispatch]);

  useEffect(() => {
    
  }, [customers]);

  const [editingCustomer, setEditingCustomer] = useState(null);
  const [changeData , setChangeData] = useState({});

  const handleEdit = (customer) => {
    setEditingCustomer(customer);
    setChangeData(customer);
  };

  const handleFieldChange = (event, fieldName) => {
    // Actualiza el cliente actual con los cambios
    setEditingCustomer(prevCustomer => ({
      ...prevCustomer,
      [fieldName]: event.target.value

    }));
    setChangeData(prevData => ({
      ...prevData,
      [fieldName]: event.target.value
    }));
    console.log(editingCustomer);
  };

  const handleSave = (customer) => {
    // Filtra los campos que han cambiado
    const changedFields = Object.keys(changeData);
    
    // Crea un nuevo objeto con solo los campos que han cambiado
    const clientData = changedFields.reduce((acc, field) => {
      acc[field] = changeData[field];
      return acc;
    }, {});
    
    dispatch(updateCustomer(clientData,clientData.dni));
    dispatch(getCustomers())
    console.log(clientData);
  
    // Realiza el patch con los datos que han cambiado (clientData)
    // Enviar clientData al servidor para el patch
  
    // Limpia los datos de edición
    setEditingCustomer(null);
    setChangeData({});
  };

  const handleCancel = () => {
    setEditingCustomer(null);
  };

  return (
    <Container maxWidth="xl" style={{ height: '100vh', overflow: 'hidden' }}>
      <Box sx={{ maxHeight: '100%', overflowY: 'auto' ,height: '100%', width: '100%' }}>
        {customers.length > 0 ? (
          <Grid container spacing={2}>
            {customers.map((customer, index) => (
              <Grid item xs={12} key={index}>
                <Card variant="outlined" sx={{
                  transition: 'transform 0.5s ease', // Transición más lenta y suave
                  '&:hover': {
                    transform: 'scale(1.05)' // Aumenta ligeramente el tamaño cuando se pasa el mouse sobre la tarjeta
                  },
                  transform: editingCustomer === customer ? 'scale(1)' : 'scale(1)' // Escala la tarjeta cuando está en modo de edición
                }}>
                  <CardContent sx={{
                    display: 'flex',
                    flexDirection: 'row',
                    gap: '20px',
                    justifyContent: 'center',
                    alignItems: 'center'
                  }}>
                    {editingCustomer === customer ? (
                      <>
                        <TextField 
                            label="Nombre"
                            value={customer.name}
                            name="name"
                            onChange={(event) => handleFieldChange(event, 'name')}
                            // Maneja cambios en el nombre
                        />
                        <TextField
                            label="DNI"
                            value={customer.dni}
                            onChange={(event) => handleFieldChange(event, 'dni')}
                            // Maneja cambios en el DNI
                        />
                        <TextField
                            label="FECHA NACIMIENTO"
                            value={customer.DateOfBirth}
                            onChange={(event) => handleFieldChange(event, 'DateOfBirth')}
                            // Maneja cambios en la fecha de nacimiento
                        />
                        <TextField
                            label="TELEFONO"
                            value={customer.phone}
                            onChange={(event) => handleFieldChange(event, 'phone')}
                            // Maneja cambios en el teléfono
                        />
                        <TextField
                            label="MAIL"
                            value={customer.mail}
                            onChange={(event) => handleFieldChange(event, 'mail')}
                            // Maneja cambios en el correo electrónico
                        />  
                       
                       <Button
                            variant="contained"
                            onClick={() => handleSave(customer)}
                            sx={{
                                backgroundColor: '#b4cbb7', // Color de fondo del botón
                                color: '#ffffff', // Color del texto del botón
                                padding: '10px 20px', // Relleno del botón
                                '&:hover': {
                                backgroundColor: '#5a5a5b', // Cambio de color al pasar el mouse sobre el botón
                                },
                            }}
                            >
                            Guardar
                        </Button>
                        <Button
                                variant="contained"
                                onClick={handleCancel}
                                sx={{
                                    backgroundColor: '#b4cbb7', // Color de fondo del botón
                                    color: '#ffffff', // Color del texto del botón
                                    padding: '10px 20px', // Relleno del botón
                                    '&:hover': {
                                    backgroundColor: '#5a5a5b', // Cambio de color al pasar el mouse sobre el botón
                                    },
                                }}
                                >
                                Cancelar
                        </Button>
                      </>
                    ) : (
                      <>
                        <Typography variant="h5" component="div" sx={{fontSize: '1rem'}}>
                          {customer.name}
                        </Typography>
                        <Typography color="text.secondary" component='div' sx={{fontSize: '1rem'}}>
                          DNI: {customer.dni}
                        </Typography>
                        {/* Agrega más detalles del cliente aquí según tu estructura de datos */}
                        <Button onClick={() => handleEdit(customer)}>Editar</Button>
                      </>
                    )}
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        ) : (
          <p>El array está vacío</p>
        )}
      </Box>
    </Container>
  );
};

export default Customers;
